#Plot 3 coords from unique list of points
points<-array( c(1,2,0,1,3,0,2,4,0,2,5,0,2,7,0,3,8,0), dim=c(3,6) ) 
tpoints<-t(points)
xx<-as.numeric(points[1,1:ncol(points)])
yy<-as.numeric(points[2,1:ncol(points)])
zz<-as.numeric(points[3,1:ncol(points)])
scatterplot3d(xx, yy, zz, highlight.3d=TRUE, col.axis="blue",
col.grid="lightblue", main="scatterplot3d - 1", pch=20)


#Plot 3 coords
x<-runif(10, 1, 100)
y<-runif(10, 1, 100)
z<-runif(10, 1, 100)
scatterplot3d(x, y, z, highlight.3d=TRUE, col.axis="blue",
col.grid="lightblue", main="scatterplot3d - 1", pch=20)
k<-cbind(x,y,z)
plot3d(k)

library("alphashape3d")
library("plot3D")

A = matrix(c(2, 4, 3, 1, 5, 7,  2,4,6,8,3,2,  3,5,7,2,4,7), nrow=6,ncol=3, byrow = TRUE) 
